$(document).ready(function(){$(".btnReply").click(function(){$(".divReplyArea").html("");$(".btnReply").show();var n=$(this).attr("data-id"),t="/home/post?parentid="+n;$(".divReplyArea[data-id="+n+"]").load(t,function(){$("form").removeData("validator");$("form").removeData("unobtrusiveValidation");$("form").each(function(){$.data($(this)[0],"validator",!1)});$.validator.unobtrusive.parse("form")});$(this).hide()});$(".vote-button").click(function(){var n=$(this).data("id"),t=$(this).data("positive");$.ajax({url:"home/vote?commentId="+n+"&positive="+t,type:"POST",async:!0,success:function(t){$(".vote-count[data-id="+n+"]").html(t)},error:function(){console.log("failed")}})})});